'use strict';

const os = require('os');
const path = require('path');
const fs = require('fs');

var counter = 0;

Object.assign(newFilename, {
  file: newFilename
});

module.exports = newFilename;

function newFilename(e) {
  var ext = e || '';
  ext = e.startsWith('.') ? e : `.${e}`
  return path.join(
    os.tmpdir(),
    `tfile-${Date.now()}-${++counter}${ext}`
  );
}
